<style>
    .on {
        background-image: url("data:image/png;base64,");
    }

    .star.star-36 .star-item {
        width: 15px;
        height: 15px;
        margin-right: 5px;
        background-size: 100% 100%;
    }

    .star .star-item {
        display: inline-block;
        background-repeat: no-repeat;
        width: 15px;
        height: 15px;
        margin-right: 5px;
        background-size: 100% 100%;
    }

    .off {
        background-image: url("data:image/png;base64,");
    }

    .half {
        background-image: url("data:image/png;base64,");
    }

    .star.star-36 .star-item:last-child {
        margin-right: 0;
    }
</style>

<template>
    <div class="star">
        <span v-for="itemClass in itemClasses" :class="itemClass" class="star-item" :key="itemClass.index"></span>
    </div>
</template>
<script>
    const LENGTH = 5;
    const CLS_ON = 'on';
    const CLS_HALF = 'half';
    const CLS_OFF = 'off';
    export default {
        props: ['score'],
        computed: {
            itemClasses() {
                let result = [];
                let score = Math.floor(this.score * 2) / 2;
                let hasDecimal = score % 1 !== 0;
                let integer = Math.floor(score);
                for (let i = 0; i < integer; i++) {
                    result.push(CLS_ON);
                }
                if (hasDecimal) {
                    result.push(CLS_HALF);
                }
                while (result.length < LENGTH) {
                    result.push(CLS_OFF);
                }
                return result;
            }
        }
    };
</script>
